class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        int r[2] = {1, 1};
        vector<pair<int, int>> v;
        for (auto &x : horizontalCut) {
            v.push_back({x, 0});
        } 
        for (auto &y : verticalCut) {
            v.push_back({y, 1});
        }
        sort(v.rbegin(), v.rend());
        long long ans = 0;
        for (auto &[val, ty] : v) {
            ans += val * r[ty ^ 1];
            r[ty] ++;
            // cout << ans << "\n";
        }
        return ans;
    }
};
