public class Solution 
{
    public long MinimumCost(int m, int n, int[] horizontalCut, int[] verticalCut)    
    {
        List<int> cuts = [];

        foreach (var cut in horizontalCut)
        {
            cuts.Add(-cut);
        }

        foreach (var cut in verticalCut)
        {
            cuts.Add(cut);
        }

        cuts.Sort((a, b) => Math.Abs(b) - Math.Abs(a));

        long h = 1, v = 1;
        long result = 0;

        foreach (var cut in cuts)
        {
            if (cut < 0)
            {
                result += (-cut) * v;
                h++;
            }
            else
            {
                result += cut * h;
                v++;
            }
        }

        return result;
    }
}
