class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCuts, vector<int>& verticalCuts) {
        sort(horizontalCuts.rbegin(), horizontalCuts.rend());
        sort(verticalCuts.rbegin(), verticalCuts.rend());

        long long h = 1; 
        long long v = 1;
        long long i = 0; 
        long long j = 0;
        long long totalCost = 0;

        while (i < horizontalCuts.size() && j < verticalCuts.size()) {
            if (horizontalCuts[i] >= verticalCuts[j]) {
                totalCost += horizontalCuts[i] * v;
                h++;
                i++;
            } else {
                totalCost += verticalCuts[j] * h;
                v++;
                j++;
            }
        }

        while (i < horizontalCuts.size()) {
            totalCost += horizontalCuts[i] * v;
            i++;
        }

        while (j < verticalCuts.size()) {
            totalCost += verticalCuts[j] * h;
            j++;
        }

        return totalCost;
    }
};
