class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        sort(h.begin(),h.end(),greater<int>());
        sort(v.begin(),v.end(),greater<int>());
        long long x=1,y=1,res=0;
        int i=0,j=0;
        while(i<m-1 && j<n-1){
            if(h[i]>v[j]){
                res+=x*h[i];
                i++;
                y++;
            }
            else{
                res+=y*v[j];
                j++;
                x++;
            }
        }
        while(i<m-1){
            res+=x*h[i++];
        }
        while(j<n-1){
            res+=y*v[j++];
        }
        return res;
    }
};
