class Solution {
    public long minimumCost(int m, int n, int[] h, int[] v) {
        Arrays.sort(h);
        Arrays.sort(v);
        int hI = m - 2,vI = n - 2,hp = 1,vp=1;        
        long ans = 0;
        while (hI >= 0 && vI >= 0) {
            if (h[hI] >= v[vI]) {
                ans += h[hI] * vp;
                hp++;
                hI--;
            } else {
                ans += v[vI] * hp;
                vp++;
                vI--;
            }
        }
        while (hI >= 0) {
            ans += h[hI] * vp;
            hI--;
        }
        while (vI >= 0) {
            ans += v[vI] * hp;
            vI--;
        }
        return ans;
    }
}
