class Solution {
private:
public:
    long long minimumCost(int m, int n, vector<int>& hc, vector<int>& vc) {
        long long ans=0;
        int vp=1,hp=1;
        sort(hc.begin(),hc.end(),greater<int>());
        sort(vc.begin(),vc.end(),greater<int>());

        int i=0,j=0;
        while((i<m-1)&&(j<n-1)){
            if(hc[i]>vc[j]){
                ans+=hc[i]*vp;
                hp++;
                i++;
            }
            else{
                ans+=vc[j]*hp;
                vp++;
                j++;
            }
        }
        while(i<m-1){
            ans+=hc[i]*vp;
            // vp++;
            i++;
        }
        while(j<n-1){
            ans+=vc[j]*hp;
            // hp++;
            j++;
        }
        return ans;
    }
};
