#include <vector>
#include <algorithm>
using namespace std;

class Solution {
public:
    long long minimumCost(long long m, long long n, vector<int>& h, vector<int>& v) {
        sort(h.rbegin(), h.rend());
        sort(v.rbegin(), v.rend());

        long long i = 0, j = 0;
        long long hp = 1, vp = 1;
        long long t = 0;

        while (i < h.size() && j < v.size()) {
            if (h[i] > v[j]) {
                t += h[i] * vp;
                hp++;
                i++;
            } else {
                t += v[j] * hp;
                vp++;
                j++;
            }
        }
        while (i < h.size()) {
            t += h[i] * vp;
            i++;
        }
        while (j < v.size()) {
            t += v[j] * hp;
            j++;
        }

        return t;
    }
};
