class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {

        sort(horizontalCut.begin(), horizontalCut.end(), greater<long long>());
        sort(verticalCut.begin(), verticalCut.end(), greater<long long>());

       long long hCuts = 1;
        long long vCuts = 1; 
        long long i = 0, j = 0;
        long long sum = 0;
        while (i < horizontalCut.size() && j < verticalCut.size()) {
            if (horizontalCut[i] > verticalCut[j]) {
                sum += horizontalCut[i] * vCuts;
                hCuts++;
                i++;
            } else {
                sum += verticalCut[j] * hCuts;
                vCuts++;
                j++;
            }
        }


        while (i < horizontalCut.size()) {
            sum += horizontalCut[i] * vCuts;
            i++;
        }
        while (j < verticalCut.size()) {
            sum += verticalCut[j] * hCuts;
            j++;
        }

        return sum;
    }
};
