class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hCut, vector<int>& vCut) {
        int h = 1, v = 1;
        ranges::sort(hCut, greater<int>{});
        ranges::sort(vCut, greater<int>{});
        int i1 = 0, i2 = 0;
        long res = 0;
        while (i1 < m-1 && i2 < n-1) {
            if (hCut[i1] < vCut[i2]) {
                res += vCut[i2] * v;
                ++h;
                ++i2;
            }
            else {
                res += hCut[i1] * h;
                ++v;
                ++i1;
            }
        }

        while (i1 < m-1) res += hCut[i1++] * h;
        while (i2 < n-1) res += vCut[i2++] * v;
        return res;
    }
};
