class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        int h = 1;
        int v = 1;
        long long ans = 0;

        priority_queue<pair<int,int>> q;

        for(auto it: horizontalCut){
            q.push({it,0});
        }
        for(auto it: verticalCut){
            q.push({it,1});
        }

        while(!q.empty()){
            int k = q.top().first;
            int l = q.top().second;
            q.pop();

            if(l==0){
                ans = ans + h * k;
                v++;
            }
            else{
                ans = ans + v * k;
                h++;
            }
        }

        return ans;    
    }
};
