class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        long long hor = 1, ver = 1;
        long long ans = 0;

        vector<pair<long long, long long>> v = {};
        for (int i = 0; i < m - 1; i++) {
            v.push_back({ horizontalCut[i], 1 });
        }
        for (int i = 0; i < n - 1; i++) {
            v.push_back({ verticalCut[i], 2 });
        }
        sort(v.begin(), v.end());

        for (int i = v.size() - 1; i >= 0; i--) {
            if (v[i].second == 1) {
                hor++;
                ans += v[i].first * ver;
            }
            else if (v[i].second == 2) {
                ver++;
                ans += v[i].first * hor;
            }
        }

        return ans;
    }
};
