class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        cuts = [(h, "h") for h in horizontalCut] + [(v, "v") for v in verticalCut]
        cuts.sort(key = lambda x: x[0], reverse = True)

        num_hor = 1
        num_ver = 1

        total_cost = 0

        for cut, cut_type in cuts:
            if cut_type == "h":
                total_cost += cut * num_ver
                num_hor += 1
            else:
                total_cost += cut * num_hor
                num_ver += 1

        return total_cost
