class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hc, vector<int>& vc) {
        sort(hc.rbegin(),hc.rend());
        sort(vc.rbegin(),vc.rend());
        int i=0,j=0,hor=1,ver=1;
        long long ans=0;
        while(i<hc.size() && j<vc.size()){
            if(hc[i]>vc[j]){
                ans+=hc[i]*hor;
                ver++;
                i++;
            }
            else{
                ans+=vc[j]*ver;
                hor++;
                j++;
            }
        }
        while(i<hc.size()){
            ans+=hc[i]*hor;
            ver++;
            i++;
        }
        while(j<vc.size()){
            ans+=vc[j]*ver;
            cout<<vc[j]<<" "<<ver<<endl;
            hor++;
            j++;
        }
        return ans;
    }
};
