class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        vector<int>hid(m-1), vid(n-1);
        iota(hid.begin(), hid.end(), 0);
        iota(vid.begin(), vid.end(), 0);
        sort(hid.begin(), hid.end(), [&](int a, int b){
            return horizontalCut[a]>horizontalCut[b];
        });
        sort(vid.begin(), vid.end(), [&](int a, int b){
            return verticalCut[a]>verticalCut[b];
        });
        long long h=1, v=1, ans=0;
        for(int ih=0, iv=0;ih<m-1||iv<n-1;){
            if(ih<m-1&&iv<n-1){
                if(horizontalCut[hid[ih]]>verticalCut[vid[iv]]){
                    ans+=h*horizontalCut[hid[ih]];
                    v++;
                    ih++;
                }
                else{
                    ans+=v*verticalCut[vid[iv]];
                    h++;
                    iv++;
                }
            }
            else if(ih<m-1){
                ans+=h*horizontalCut[hid[ih]];
                v++;
                ih++;
            }
            else{
                ans+=v*verticalCut[vid[iv]];
                    h++;
                    iv++;
            }
        }
        return ans;
    }
};
