class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        sort(h.begin(),h.end(),greater<int>());
        sort(v.begin(),v.end(),greater<int>());
        long long i=0,j=0;
        long long hp=1ll,vp=1ll;
        long long ans=0;
        m--,n--;
        while(i<m && j<n){
            if(h[i]>v[j]){
                ans+=((long long)h[i]*vp);
                hp++;
                i++;
            }
            else{
                ans+=((long long)v[j]*hp);
                vp++;
                j++;
            }
        }
        // cout<<i<<" "<<j<<" "<<ans<<endl;
        while(i<m){
            ans+=(long long)h[i]*vp;
            i++;
        }
        while(j<n){
            ans+=(long long)v[j]*hp;
            j++;
        }
        return ans;
    }
};
