class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hc, vector<int>& vc) {
        long long hp=1;
        long long vp=1;
        long long cost=0;
        sort(hc.begin(),hc.end());
        sort(vc.begin(),vc.end());
        long long i=hc.size()-1,j=vc.size()-1;
        while(i>=0 and j>=0){
            if(hc[i]>vc[j]){
                cost+= (hp*hc[i]);
                i--;
                vp++;
            }
            else{
                cost+= (vp*vc[j]);
                j--;
                hp++;
            }
        }
        while(i>=0){
             cost+= (hp*hc[i]);
             i--;
             vp++;
        }
        while(j>=0){
              cost+= (vp*vc[j]);
              j--;
              hp++;
        }
        return cost;

    }
};
