class Solution {
public:
    long long minimumCost(int p, int q, vector<int>& horizontalCut, vector<int>& verticalCut) {
        long long i = 0, j = 0,hp=1,vp=1,total=0;
        sort(horizontalCut.rbegin(), horizontalCut.rend());
        sort(verticalCut.rbegin(), verticalCut.rend());
        long long m = horizontalCut.size(),n=verticalCut.size();
        while (i< m && j < n) {
            if (horizontalCut[i]>verticalCut[j]) {
                total += horizontalCut[i]*vp;
                hp++;
                i++;
            } else {
                total += verticalCut[j]*hp;
                vp++;
                j++;
            }
        }        
        while (i < m) {
            total += horizontalCut[i]*vp;
            i++;
        }        
        while (j < n) {
            total += verticalCut[j]*hp;
            j++;
        }        
        return total;
    }
};
