type Cuts struct {
    cost int
    isHorizontal bool
}

func minimumCost(m int, n int, horizontalCut []int, verticalCut []int) int64 {
    cuts := make([]Cuts, 0, len(horizontalCut) + len(verticalCut))
    for _, cost := range horizontalCut {
        cuts = append(cuts, Cuts{cost, true})
    }
    for _, cost := range verticalCut {
        cuts = append(cuts, Cuts{cost, false})
    }
    sort.Slice(cuts, func(i, j int) bool { return cuts[i].cost > cuts[j].cost})
    var totalCost int64
    var horizontalNum int64 = 1
    var verticalNum int64 = 1
	for _, cut := range cuts {
		if cut.isHorizontal {
			totalCost += int64(cut.cost) * verticalNum
			horizontalNum++
		} else {
			totalCost += int64(cut.cost) * horizontalNum
			verticalNum++
		}
	}
    return totalCost
}
