class Solution:
  def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
    cuts = []
    for i in range(len(horizontalCut)):
      cuts.append((horizontalCut[i], True))
    for i in range(len(verticalCut)):
      cuts.append((verticalCut[i], False))
    cuts.sort(reverse=True)
    counth = countv = cost = 0
    for c, isHorizontalCut in cuts:
      if isHorizontalCut:
        cost += c * (countv + 1)
        counth += 1
      else:
        cost += c * (counth + 1)
        countv += 1
    return cost
