class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        v.push_back(0);
        h.push_back(0);

        sort(v.begin(), v.end());
        reverse(v.begin(), v.end());
        sort(h.begin(), h.end());
         reverse(h.begin(), h.end());


        int hi = 0, vi = 0;
        int hp = 1, vp = 1;
        long long cost = 0;

        while (hi < m - 1 || vi < n - 1) {
            // if(p)
            // {

            // }
            if (vi >= n - 1 || (hi < m - 1 && h[hi] > v[vi])) {
                //cout<<cost;
                cost += h[hi] * vp;
                //cout<<h[hi];
                hi++;
                hp++;
            } else {
                cost += v[vi] * hp;
                //cout<<hp;
                vi++;
                vp++;
            }
            //cout<<hi<<" "<<hp<<" "<<vi;
        }

        return cost;
    }
};
