class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        vector<pair<int,int>>costs;
        for(auto i:h) costs.push_back({i,0});
        for(auto i:v) costs.push_back({i,1});
        sort(costs.rbegin(),costs.rend());
        int ho = 1, vt = 1;
        long long total = 0;
        for(auto i:costs){
            if(i.second == 0){
                total += i.first*vt;
                ho++;
            }
            else{
                total+=i.first*ho;
                vt++;
            }
        }
        return total;
    }
};
