class Solution {
    public long minimumCost(int m, int n, int[] hr, int[] vr){
        int hi = 1;
        int vi = 1;
        long ans = 0;
        int i = hr.length - 1;
        int j = vr.length - 1; 
        Arrays.sort(hr);
        Arrays.sort(vr);
        // System.out.println(Arrays.toString(hr));
        while(i >= 0 && j >= 0){
    if(hr[i] >= vr[j]){
                ans += (hr[i] * vi);
                i--;
                hi++;
        }else{
                ans += (vr[j] * hi);
                j--;
                vi++;
            }
        }
        // System.out.println(ans);
        while(i >= 0){ 
              ans += (hr[i] * vi);
                i--;    
                hi++;
        }
        // System.out.println(ans);
        while(j >= 0){
            ans += (vr[j] * hi);
            j--;
            vi++;
        }
        return ans;
    }
}
