class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        vector<pair<int, char>> vp;
        for (int &x: horizontalCut) vp.push_back({x, 'h'});
        for (int &x: verticalCut) vp.push_back({x, 'v'});

        sort(vp.rbegin(), vp.rend());

        int horis = 1, vertis = 1;

        long long result = 0;
        for (auto &x: vp) {
            if (x.second == 'h') {
                result += (x.first*vertis);
                horis++;
            } else {
                result += (x.first*horis);
                vertis++;
            }
        }

        return result;
    }
};
