class Solution {
public:
    long long int minimum(vector<int> &X, vector<int> &Y, long long int m, long long int n) 
    { 
       long long  int res = 0; 


        sort(begin(X),end(X), greater<int>()); 


        sort(begin(Y),end(Y), greater<int>()); 


        long long int hzntl = 1, vert = 1; 


        long long int i = 0, j = 0; 
        while (i < m && j < n) 
        { 
            if (X[i] > Y[j]) 
            { 
                res += X[i] * vert; 


                hzntl++; 
                i++; 
            } 
            else
            { 
                res += Y[j] * hzntl; 


                vert++; 
                j++; 
            } 
        } 


        long long int total = 0; 
        while (i < m) 
            total += X[i++]; 
        res += total * vert; 


        total = 0; 
        while (j < n) 
            total += Y[j++]; 
        res += total * hzntl; 

        return res; 
    }


    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        long long int cake= minimum(horizontalCut,verticalCut,m-1,n-1);
        return cake;
    }
};
