class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
         vector<pair<int,int>> vec;
        for(auto &ab:h)
            {
            vec.push_back({ab,0});
            }
        for(auto &ab:v)
            {
            vec.push_back({ab,1});
            }
        sort(vec.begin(),vec.end());
        int ct=0;
        int t=0;
        long long int ans=0;
        for(int i=vec.size()-1;i>=0;i--)
            {
               if(vec[i].second==0)
                   {
                     ans+=(vec[i].first*1LL*(t+1));
                   ct++;
                   }
            else
                {
                ans+=(vec[i].first*1LL*(ct+1));
                t++;
                }
            }
        return ans;
    }
};
