class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        sort(h.rbegin(),h.rend());
        sort(v.rbegin(),v.rend());
        int horiz=1,vertical=1;
        int i=0,j=0;
        long long ans=0;
        while(i<m-1&&j<n-1){
            if(h[i]>v[j]){
                ans+=(long long)(vertical)*(h[i]);
                horiz++;i++;
            }
            else if(v[j]>h[i]){
                ans+=(long long)(horiz)*(v[j]);
                vertical++;j++;
            }
            else{
                if(vertical<horiz){
                    ans+=(long long)(vertical)*(h[i]);
                    horiz++;i++;
                }
                else{
                    ans+=(long long)(horiz)*(v[j]);
                    vertical++;j++;
                }
            }
        }
        while(i<m-1){
            ans+=(long long)(vertical)*(h[i]);
            horiz++;i++;
        }
        while(j<n-1){
            ans+=(long long)(horiz)*(v[j]);
            vertical++;j++;
        }
        return ans;
    }
};
