class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hCut, vector<int>& vCut) {
        long long h = 1, v = 1;
        vector<pair<long long, long long>> hor, ver;
        for(int i=0; i<hCut.size(); i++)
            hor.push_back({hCut[i], i});
        for(int i=0; i<vCut.size(); i++)
            ver.push_back({vCut[i], i});
        sort(hor.begin(), hor.end(), greater<pair<long long, long long>>());
        sort(ver.begin(), ver.end(), greater<pair<long long, long long>>());

        int i=0, j=0;
        long long ans = 0;
        while(i < hCut.size() && j < vCut.size()) {
            if(hor[i].first > ver[j].first) {
                ans += (long long)hor[i++].first * (long long)h;
                v++;
            }
            else if(hor[i].first < ver[j].first) {
                ans += (long long)ver[j++].first * (long long)v;
                h++;
            }
            else if(hor[i].second > ver[j].second) {
                ans += (long long)hor[i++].first * (long long)h;
                v++;
            } 
            else {
                ans += (long long)ver[j++].first * (long long)v;
                h++;
            }
        }

        while(i < hCut.size()) {
            ans += (long long)hor[i++].first * (long long)h;
        }

        while(j < vCut.size()) {
            ans += (long long)ver[j++].first * (long long)v;
        }

        return ans;
    }
};
