class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hc, vector<int>& vc) {
        sort(hc.begin(),hc.end());
        sort(vc.begin(),vc.end());
        int row=1;
        int col=1;
        int ihc=hc.size()-1;
        int ivc=vc.size()-1;
        long long total=0;
        while(ihc>=0 && ivc>=0){
            if(hc[ihc]>vc[ivc]){
                row++;
                long long p=col*hc[ihc];
                total+=(p);
                ihc--;
            }
            else{
                col++;
                long long p=row*vc[ivc];
                total+=(p);
                ivc--;
            }
        }
         while(ihc>=0){
             long long p=col*hc[ihc];
             total+=(p);
             ihc--;
        }

         while(ivc>=0){
             long long p=row*vc[ivc];
             total+=(p);
             ivc--;
        }
        return total;
    }
};
