class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:

        cuts = sorted([(cost, i, 0) for i, cost in enumerate(horizontalCut)] + [(cost, i, 1) for i, cost in enumerate(verticalCut)], reverse=True)

        total_cost = 0
        h_cuts, v_cuts = 0, 0
        for cost, i, cut in cuts:
            if cut == 0:
                h_cuts += 1
                factor = v_cuts
            else:
                v_cuts += 1
                factor = h_cuts

            total_cost += cost * (factor+1)

        return total_cost
