class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        cut_cost = []
        for c in horizontalCut:
            cut_cost.append((c, 'h'))
        for c in verticalCut:
            cut_cost.append((c, 'v'))     
        cut_cost.sort(key=lambda x: -x[0])
        total_cost = 0
        v_cuts = 1
        h_cuts = 1
        for cost, direction in cut_cost:
            if direction == 'h':
                total_cost += cost * v_cuts
                h_cuts += 1
            elif direction == 'v':
                total_cost += cost * h_cuts
                v_cuts += 1
        return total_cost
