using llint = long long;
#define alll(x) x.begin(), x.end()

class Solution
{
public:
    llint minimumCost(int m, int n, vector<int> &hpcs, vector<int> &vpcs)
    {
        sort(alll(hpcs));
        sort(alll(vpcs));
        reverse(alll(hpcs));
        reverse(alll(vpcs));
        llint hor_pieces = 0, ver_pieces = 0, hor_idx = 0, ver_idx = 0, result = 0;
        hor_pieces++;
        ver_pieces++;
        while (true)
        {
            if (hor_idx == hpcs.size() || ver_idx == vpcs.size())
                break;
            if (hpcs[hor_idx] > vpcs[ver_idx])
            {
                result += hpcs[hor_idx] * ver_pieces;
                hor_idx++;
                hor_pieces++;
            }
            else
            {
                result += vpcs[ver_idx] * hor_pieces;
                ver_idx++;
                ver_pieces++;
            }
        }
        while (true)
        {
            if (hor_idx == hpcs.size())
                break;
            result += hpcs[hor_idx] * ver_pieces;
            hor_idx++;
            hor_pieces++;
        }
        while (true)
        {
            if (ver_idx == vpcs.size())
                break;
            result += vpcs[ver_idx] * hor_pieces;
            ver_idx++;
            ver_pieces++;
        }
        return result;
    }
};
