class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& ho, vector<int>& ve) {
        vector<pair<int,char>> cuts;
        for(int i=0;i<m-1;i++){
            cuts.push_back({ho[i],'H'});
        }
        for(int i=0;i<n-1;i++){
            cuts.push_back({ve[i],'V'});
        }
        sort(cuts.rbegin(),cuts.rend());
        int h = 1;
        int v = 1;
        long long res =0;
        for(auto x:cuts){
            int cost = x.first;
            char direction = x.second;
            if(direction == 'V'){
                res+= (cost*h);
                v++;
            }
            else{
                res+=(cost*v);
                h++;
            }
        }
        return res;

    }
};
