class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        int i=0;
        int j=0;
        int hc=1 , vc=1;
        long long ans=0;
        vector<int> h,v;
        for(int i=0 ; i<m-1 ; i++)
        {
            h.push_back(horizontalCut[i]);
        }
        for(int j=0 ; j<n-1 ; j++)
        {
            v.push_back(verticalCut[j]);
        }
        sort(h.begin() , h.end() , greater<int>());
        sort(v.begin() , v.end() , greater<int>());
        while(i<m-1 && j<n-1)
        {
            if(h[i] <= v[j])
            {   
                ans += v[j]*hc;
                j++;
                vc++;
            }
            else
            {
                ans += h[i]*vc;
                i++;
                hc++;
            }
        }
        while(i<m-1)
        {
            ans += h[i]*vc;
            i++;
        }
        while(j<n-1)
        {
            ans += v[j]*hc;
            j++;
        }

        return ans;
    }
};
