class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {

        long long res = 0LL;
        sort(horizontalCut.begin(), horizontalCut.end(), greater<int>());
        sort(verticalCut.begin(), verticalCut.end(), greater<int>());
        m--;
        n--;
        long long horizontalCount = 1LL, verticalCount = 1LL;
        long long i = 0, j = 0;

        while (i < m && j < n)
        {
            if (horizontalCut[i] > verticalCut[j])
            {
                res += 1LL * horizontalCut[i] * verticalCount;
                horizontalCount++;
                i++;
            }

            else
            {
                res += 1LL * verticalCut[j] * horizontalCount;
                verticalCount++;
                j++;
            }
        }

        long long ans = 0LL;
        while (i < m)
            ans += 1LL * horizontalCut[i++];
        res += 1LL * ans * verticalCount;

        ans = 0;
        while (j < n)
            ans += 1LL * verticalCut[j++];
        res += 1LL * ans * horizontalCount;

        return res;
    }
};
