class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCuts, vector<int>& verticalCuts) {
         sort(horizontalCuts.begin(), horizontalCuts.end(), greater<int>());
        sort(verticalCuts.begin(), verticalCuts.end(), greater<int>());

        int hIndex = 0, vIndex = 0;
        int hParts = 1, vParts = 1;
        long long totalCost = 0;

    while (hIndex < horizontalCuts.size() && vIndex < verticalCuts.size()) {
        if (horizontalCuts[hIndex] > verticalCuts[vIndex]) {
            totalCost += horizontalCuts[hIndex] * vParts;
            hParts++;
            hIndex++;
        } else {
            totalCost += verticalCuts[vIndex] * hParts;
            vParts++;
            vIndex++;
        }
    }
    while (hIndex < horizontalCuts.size()) {
        totalCost += horizontalCuts[hIndex] * vParts;
        hIndex++;
    }
    while (vIndex < verticalCuts.size()) {
        totalCost += verticalCuts[vIndex] * hParts;
        vIndex++;
    }

    return totalCost;
}

};
