class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {

        long long int h=1;
        long long int v=1;

        multiset<pair<long long int,char>>s;

        for(long long int i=0;i<m-1;i++) s.insert({-1*horizontalCut[i],'H'});
        for(long long int i=0;i<n-1;i++) s.insert({-1*verticalCut[i],'V'});

        long long int ans=0;
        for(auto i = s.begin();i!=s.end();i++){

            if(i->second == 'H'){
                ans+=h*i->first*-1;
                v++;
            }
            else{
                ans+=v*i->first*-1;
                h++;
            }
        }

        return ans;

    }
};
