class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        sort(horizontalCut.rbegin(), horizontalCut.rend());
        sort(verticalCut.rbegin(), verticalCut.rend());

        // Initialize pieces and total cost
        int horizontalPieces = 1;
        int verticalPieces = 1;
        long long totalCost = 0;

        // Use indices to traverse the cuts
        int i = 0, j = 0;
        while (i < horizontalCut.size() && j < verticalCut.size()) {
            if (horizontalCut[i] > verticalCut[j]) {
                totalCost += (long long)horizontalCut[i] * verticalPieces;
                horizontalPieces++;
                i++;
            } else {
                totalCost += (long long)verticalCut[j] * horizontalPieces;
                verticalPieces++;
                j++;
            }
        }

        // Add remaining horizontal cuts if any
        while (i < horizontalCut.size()) {
            totalCost += (long long)horizontalCut[i] * verticalPieces;
            i++;
        }

        // Add remaining vertical cuts if any
        while (j < verticalCut.size()) {
            totalCost += (long long)verticalCut[j] * horizontalPieces;
            j++;
        }

        return totalCost;
    }
};
