class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& v1, vector<int>& v2) {
        vector<pair<int,char>>nums;        
        for(int i=0;i<v1.size();i++) nums.push_back({v1[i],'H'});
        for (int j = 0; j < v2.size(); j++) nums.push_back({v2[j],'V'});
        sort(nums.begin(),nums.end(),greater<pair<int, char>>());        
        int h=1,v=1;
        long long ans=0;     
        for(int i=0;i<nums.size();i++){
            if (nums[i].second=='H'){
                ans+=nums[i].first*v;
                h++;
            } 
            else{
                ans+=nums[i].first*h;
                v++;
            }
        }       
        return ans;
    }
};
