class Solution {
public:

    static bool comp(int& a , int& b){
        return a>b;
    }

    long long minimumCost(int m, int n, vector<int>& hcut, vector<int>& vcut) {
        long long h = 0, v = 0;
        sort(hcut.begin(), hcut.end(),comp);
        sort(vcut.begin(), vcut.end(),comp);

        long long ans = 0;
        int i = 0, j = 0;
        while(i<(m-1) and j<(n-1)){
            if(hcut[i] >= vcut[j]){
                ans += hcut[i++] *1LL* (v+1);
                h++;
            }
            else{
                ans += vcut[j++]*1LL *(h+1);
                v++;
            }
        }
        while(i<(m-1)){
            ans += hcut[i++] *1LL* (v+1);
            h++;
        }
        while(j<(n-1)){
            ans += vcut[j++] *1LL*(h+1);
            v++;
        }
        return ans;
    }
};
