class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        vector<pair<long long ,long long>>cuts;
        for (auto it:horizontalCut) cuts.push_back({it, 0});
        for (auto it :verticalCut) cuts.push_back({it, 1});  
        sort(cuts.rbegin(),cuts.rend());

        long long int h=1;
        long long int v=1;
        long long int ans=0;

        for(auto c:cuts){
            int cost=c.first;
            int type=c.second;

            if (type==0){
                ans+=cost*v;
                h++;
            } 
            else{
                ans+=cost*h;
                v++;
            }
        }

        return ans;
    }
};
