class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        if(m==1&&n==1)return 0;
        long long cost=0;
        priority_queue<int>q1,q2;
        for(int i=0;i<h.size();i++)
            q1.push(h[i]);
        for(int i=0;i<v.size();i++)
            q2.push(v[i]);
        int hor=0,ver=0;
        while(!q1.empty()&&!q2.empty()){
            if(q1.top()>q2.top()){
                int x=q1.top();
                q1.pop();
                hor++;
                cost+=x*(ver+1);
            }
            else{
                int y=q2.top();
                q2.pop();
                ver++;
                cost+=y*(hor+1);
            }
        }
        while(!q1.empty()){
            int x=q1.top();
            q1.pop();
            cost+=x*(ver+1);
        }
        while(!q2.empty()){
            int y=q2.top();
            q2.pop();
            cost+=y*(hor+1);
        }
        return cost;
    }
};
