class Solution {
public:
    using ll = long long;
    using pii = pair<int, int>;
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        ll ans = 0;
        // 0 1
        priority_queue<pii> pq;
        for (int i = 0; i < m - 1; i++) {
            pq.emplace(horizontalCut[i], 0);
        }
        for (int j = 0; j < n - 1; j++) {
            pq.emplace(verticalCut[j], 1);
        }
        vector<int> cnt(2);
        while (!pq.empty()) {
            auto [x, v] = pq.top();
            pq.pop();
            ans += 1ll * x * (cnt[v ^ 1] + 1);
            cnt[v]++;
        }
        return ans;
    }
};
