class Solution {
public:
    vector<long long int> h,v;
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        for(auto it:horizontalCut) h.push_back(it);
        for(auto it:verticalCut) v.push_back(it);
        sort(h.begin(),h.end());
        sort(v.begin(),v.end());
        long long int cnt1=1,cnt2=1;
        long long int i=h.size(),j=v.size(),ans=0;i--;j--;
        while(i>=0 && j>=0){
            if(h[i]<v[j]){
                ans+=(v[j]*cnt1);
                cnt2++;
                j--;
            }
            else if(h[i]>v[j]){
                ans+=(h[i]*cnt2);
                cnt1++;
                i--;
            }
            else{
                if(cnt1<cnt2){
                    ans+=(v[j]*cnt1);
                    cnt2++;
                    j--;
                }
                else{
                    ans+=(h[i]*cnt2);
                    cnt1++;
                    i--;
                }
            }
        }
        while(i>=0){
            ans+=(h[i]*cnt2);
            cnt1++;
            i--;
        }
        while(j>=0){
            ans+=(v[j]*cnt1);
            cnt2++;
            j--;
        }
        return ans;
    }
};
