class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        vector<pair<int, char>> cuts;

        for (int cost : horizontalCut) {
            cuts.emplace_back(cost, 'h');
        }
        for (int cost : verticalCut) {
            cuts.emplace_back(cost, 'v');
        }
        sort(cuts.begin(), cuts.end(), greater<>());

        int horizontalPieces = 1;
        int verticalPieces = 1;
        long long totalCost = 0;

        for (const auto& cut : cuts) {
            if (cut.second == 'h') {
                totalCost += static_cast<long long>(cut.first) * verticalPieces;
                horizontalPieces++;
            } else {
                totalCost += static_cast<long long>(cut.first) * horizontalPieces;
                verticalPieces++;
            }
        }
        return totalCost;
    }
};
