class Solution {
public:
    using ll = long long;

    long long minimumCost(int row, int col, vector<int>& hCuts, vector<int>& vCuts) {

        sort(hCuts.rbegin(), hCuts.rend());
        sort(vCuts.rbegin(), vCuts.rend());

        ll hIndex = 0, vIndex = 0;
        ll hPieces = 1, vPieces = 1;
        ll ans = 0;

        while (hIndex < hCuts.size() && vIndex < vCuts.size()) {
            if (hCuts[hIndex] > vCuts[vIndex]) {
                ans += hCuts[hIndex] * vPieces;
                hPieces++;
                hIndex++;
            } else {
                ans += vCuts[vIndex] * hPieces;
                vPieces++;
                vIndex++;
            }
        }

        while (hIndex < hCuts.size()) {
            ans += hCuts[hIndex] * vPieces;
            hIndex++;
        }


        while (vIndex < vCuts.size()) {
            ans += vCuts[vIndex] * hPieces;
            vIndex++;
        }

        return ans;
    }
};
