class Solution {
    public long minimumCost(int m, int n, int[] hc, int[] vc) {
        Arrays.sort(hc); Arrays.sort(vc);

        int h= m-2, v= n-2;
        long hcnt= 1, vcnt= 1;
        long cost = 0;

        while (h >= 0 && v >= 0) {
            if (hc[h] >= vc[v]) {
                cost += vcnt * hc[h--];
                hcnt++;
            } else {
                cost += hcnt * vc[v--];
                vcnt++; 
            }
        }

        while (h >= 0) {
            cost += vcnt * hc[h--];
            hcnt++;
        }
        while (v >= 0) {
            cost += hcnt * vc[v--];
            vcnt++;
        }

        return cost;
    }
}
