class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hvec, vector<int>& vvec) {
        int hcut=1;
        int vcut=1;
        int times=m+n-2;
        int j=0,k=0;
        long long ans=0;
        sort(hvec.begin(),hvec.end(),greater<int>());
        sort(vvec.begin(),vvec.end(),greater<int>());
        for (int i=0;i<times;i++){
            if (j<(m-1) and k<(n-1)){
                if (hvec[j]>vvec[k]){
                    ans+=(hvec[j]*hcut);
                    vcut++;
                    j++;
                }
                else {
                    ans+=(vvec[k]*vcut);
                    hcut++;
                    k++;
                }
            }
            else if (j<m-1){
                ans+=(hvec[j]*hcut);
                vcut++;
                j++;
            }
            else {
                ans+=(vvec[k]*vcut);
                hcut++;
                k++;
            }
        }
        return ans;
    }
};
