class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        long long int h = 1, v = 1, ans = 0;
        auto& hc =  horizontalCut;
        auto& vc =  verticalCut;
        sort(hc.rbegin(),hc.rend());
        sort(vc.rbegin(),vc.rend());

        // iterator
        int i=0,j=0;

        while(i<m-1 && j<n-1)
        {
            if(hc[i]<vc[j])
            {
                ans+= h * vc[j];
                v++;
                j++;
            }
            else
            {
                ans+=hc[i]*v;
                i++;
                h++;
            }
        }

        while(j<n-1)
        {
           ans+= h * vc[j++];
        } 

        while(i<m-1)
        {
            ans+=hc[i++]*v;
        }

        return ans;
    }
};
