class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hc, vector<int>& vc) {
                vector<int> a, b;
        for (int i = 0; i < m - 1; ++i) {
            a.push_back(hc[i]);
        }
        for (int i = 0; i < n - 1; ++i) {
            b.push_back(vc[i]);
        }
        sort(a.rbegin(), a.rend());
        sort(b.rbegin(), b.rend());
        int ac = 0;
        int bc = 0;
        int i = 0;
        int j = 0;
        long long ans = 0;
        while (i < m - 1 &&  j < n - 1) {
            if ( a[i] >= b[j]) {
                ans += a[i]+ (bc * a[i]);
                ++ac;
                ++i;

            } else  {
                ans += b[j] + (ac * b[j]);
                ++bc;
                ++j;
            }
        }
        while (i < m - 1 ) {

                ans += a[i] + (bc * a[i]);
                ++ac;
                ++i;


        }
         while (j < n - 1) {

                ans += b[j]+ (ac * b[j]);
                ++bc;
                ++j;

        }
        return ans;
    }
};
