bool isPalindrome(string s, int start, int end)
{
    while (start < end)
    {
        if (s[start] != s[end])
            return false;
        start++;
        end--;
    }
    return true;
}

int digitSum(int n)
{
    int sum = 0;
    while (n > 0)
    {
        int digit = n % 10;
        sum += digit;
        n /= 10;
    }
    return sum;
}

class Solution {
public:
    bool isPrime(int number) {
        if (number <= 1) {
            return false;
        }
        for (int i = 2; i <= std::sqrt(number); ++i) {
            if (number % i == 0) {
                return false;
            }
        }
        return true;
    }

    long long minimumCost(int m, int n, std::vector<int>& hc, std::vector<int>& vc) {
        long long hp = 1, vp = 1, tc = 0;
        sort(hc.rbegin(), hc.rend());
        sort(vc.rbegin(), vc.rend());
         int hi = 0, vi = 0;
        while (hi < hc.size() && vi < vc.size()) {
            if (hc[hi] >= vc[vi]) {
                tc += LL(hc[hi]) * vp;
                hi++;
                hp++;
            } else {
                tc += LL(vc[vi]) * hp;
                vi++;
                vp++;
            }
        }

         while (vi < vc.size()) {
            tc += LL(vc[vi]) * hp;
            vi++;
        }


        while (hi < hc.size()) {
            tc += LL(hc[hi]) * vp;
            hi++;
        }


        return tc;
    }
};
